 function renderElement(ajaxResponse) {

    // turn string response to JSON array
    var responseArray = JSON.parse(ajaxResponse);
    
  
     
    // make sure there is a response
    if (responseArray.length > 0) {

        // get container
        var container = document.getElementById("container");

        // iterate over each response
        
        for (var i = 0; i < responseArray.length; i += 1) {
            
                // create the elems needed
                var element = document.createElement("li");
                element.className = "element";
                element.id = "elementid"+[i];
                
    
                var image= document.createElement("img");
                image.className = "port-images lazy";
                image.alt = responseArray[i].title;
                image.src = "img/preloader.gif";
                image.setAttribute("data-original", responseArray[i].image);
    
                var title = document.createElement("h3");
                title.className = "title";
                title.innerHTML = responseArray[i].title;
    
                var body = document.createElement("p");
				body.className = "body";
                body.innerHTML = responseArray[i].body;
                
                // append them all to player wrapper
                element.appendChild(image);
                element.appendChild(title);
                element.appendChild(body);
                
                if (responseArray[i].link != '#') {
	                 var link = document.createElement("a");
					 link.className = "button-link";
					 link.href = responseArray[i].link;
					 link.target = "_blank";
					 link.innerHTML = "VIEW ONLINE";
					 element.appendChild(link);
                }
               
    

                
    
                // append player to container
                container.appendChild(element);
				
                   $("#elementid" + i).hide();
               
                
        }
    
    }     
    
      $(document).ready(function(){
    completedLoad (responseArray.length);
    });
} 

